    /*
     * Deleting aset_tetap
     */
    function remove($id_aset_tetap)
    {
        $aset_tetap = $this->Aset_tetap_model->get_aset_tetap($id_aset_tetap);

        // check if the aset_tetap exists before trying to delete it
        if(isset($aset_tetap['id_aset_tetap']))
        {
            $this->Aset_tetap_model->delete_aset_tetap($id_aset_tetap);
            redirect('aset_tetap/index');
